<template>
<body>
  <div class="main">
    <h1 class="title" v-if="mesageDefault">Escanee el QR que encontrará en la línea de trabajo correspondiente, que se parece a éste.</h1>
    <h1 v-if="mesageError" class="errorMessage">ESCANEE UN QR NO UN CÓDIGO DE BARRAS POR FAVOR</h1>
    <input
      name="barcode"
      v-model="lineUrl"
      v-on:keyup.enter="goToLine(this.lineUrl)"
      ref="myInput"
      autofocus
      type="text"
      id="urlLinea"
      placeholder="URL de la linea"
      
    />
    <div class="imgContainer">
      <img src="@/assets/img/qr_buena_prueba_viernes.png" alt="QR" class="qrCode">
    </div>
  <div class="routerLink">
      <button class="button" @click="goToSelectLine()">O elige manualmente</button>
  </div>
  </div>
</body>
  
  
</template>

<script>
export default {
  data(){
    return{
      lineUrl:"",
      mesageError:false,
      mesageDefault:true,
    }
  },
  mounted(){
    this.setFocus();
  },
  methods:{
    setFocus() {
      this.$refs.myInput.focus();
    },
    goToSelectLine(){
      this.$router.push("/selectLine")
    },
    goToLine(lineUrl){
      if(lineUrl.startsWith("http")){
        location.href = lineUrl
      } else {
        this.mesageDefault= false
        this.mesageError = true
        this.lineUrl= "";
        this.setFocus();
        
        
      }
     
      
    }
  }
}
</script>

